mapper.select:
select sum(watersum) from O_CAMPUSWATER 
where watertype=1 
and (select to_char(WATERDATE,'yyyy-mm') from dual)=#{formatDate}

select sum(watersum) from O_CAMPUSWATER 
where watertype=0 
and (select to_char(WATERDATE,'yyyy-mm') from dual)=#{formatDate}

select
from o_campuswater cw join (select * from o_subject) sb on cw.subjectid=sb.subjectid join (select * from o_schooldistrict) sls on cw.schoolid=sls.schoolid join (select * from o_subjectdetail) sbd on cw.subjectdetailid=sbd.subjectdetailid
and waterdate >=#{startDate}
and waterdate <=#{endDate}

 o_campuswater cw


	and waterdate >=#{startDate}


	and waterdate <=#{endDate}
